import jsonimport socketHOST = 'localhost'    # The remote hostPORT = 9090              # The same port as used by the serverwith socket.socket(socket.AF_INET, socket.SOCK_STREAM) as s:    s.connect((HOST, PORT))    a = input()    k = 1    while True:        str1 = 'requirement_' + str(k)        k += 1        i = {str1: int(a)}        b = json.dumps(i).encode('utf-8')        s.sendall(b)        data = s.recv(1024)        data = json.loads(data.decode('utf-8'))        print(data)        a = input()